"use strict";
const octokit_1 = require("./lib/octokit");
const helper_1 = require("./lib/helper");
const PluginName = 'picgo-plugin-gitee-uploader';
const UploaderName = 'gitee';
function initOcto(ctx) {
    const options = ctx.getConfig('picBed.' + UploaderName);
    if (!options) {
        throw new Error("Can't find gitee config:" + ('picBed.' + UploaderName));
    }
    const ins = octokit_1.getIns(options, ctx);
    return ins;
}
function notic(showNotification, title, body) {
    showNotification({
        title: 'gitee: ' + title,
        body
    });
}
const SyncGithubMenu = {
    label: 'Sync gitee',
    async handle(ctx, { showNotification }) {
        const octokit = initOcto(ctx);
        notic(showNotification, 'Sync gitee...');
        const githubDataJson = await octokit.getDataJson().catch(e => {
            ctx.log.error(e);
            notic(showNotification, 'Error at load dataJson', e.message);
            throw e;
        });
        const uploaded = ctx.getConfig('uploaded');
        const localDataJson = {
            data: uploaded.filter(each => each.type === UploaderName).map(helper_1.zip),
            lastSync: (ctx.getConfig(PluginName) || {}).lastSync
        };
        const { sha, lastSync, data } = githubDataJson;
        if (localDataJson.lastSync > lastSync) {
            try {
                if (sha) {
                    await octokit.updateDataJson({
                        data: localDataJson,
                        sha
                    });
                }
                else {
                    await octokit.createDataJson(localDataJson);
                }
            }
            catch (e) {
                ctx.log.error(e);
                notic(showNotification, 'Error at sync github', e.message);
                throw e;
            }
        }
        else {
            const newUploaded = data
                .map(each => {
                const obj = helper_1.unzip(each);
                return Object.assign(Object.assign({}, obj), { type: UploaderName, imgUrl: octokit.parseUrl(obj.fileName) });
            })
                .concat(uploaded.filter(each => each.type !== UploaderName));
            ctx.saveConfig({
                uploaded: newUploaded,
                [PluginName]: {
                    lastSync
                }
            });
        }
        notic(showNotification, 'Sync successful', 'Succeed to sync gitee');
    }
};
const PullGithubMenu = {
    label: 'Pull gitee',
    handle: async (ctx, { showNotification }) => {
        const octokit = initOcto(ctx);
        notic(showNotification, 'Pull img from gitee...');
        try {
            const { tree } = await octokit.getPathTree();
            const imgList = tree
                .filter(each => /\.(jpg|png|jpeg|gif)$/.test(each.path))
                .map(each => {
                const unzipImg = helper_1.unzip({
                    f: each.path,
                    s: each.sha
                });
                return Object.assign(Object.assign({}, unzipImg), { type: UploaderName, imgUrl: octokit.parseUrl(each.path) });
            });
            const uploaded = ctx
                .getConfig('uploaded')
                .filter(each => each.type !== UploaderName);
            uploaded.unshift(...imgList);
            ctx.saveConfig({
                uploaded,
                [PluginName]: {
                    lastSync: helper_1.getNow()
                }
            });
            notic(showNotification, 'Pull successful', 'Succeed to pull from gitee');
        }
        catch (e) {
            ctx.log.error(e);
            notic(showNotification, 'Error at pull from gitee', e.message);
        }
    }
};
const guiMenu = ctx => {
    return [SyncGithubMenu, PullGithubMenu];
};
const handle = async (ctx) => {
    ctx.log.info('插件开始上传.....');
    let output = ctx.output;
    output.map(i => {
        if (!i.extname) {
            ctx.log.warn('这个图片没有扩展名,将会默认设置jpg');
            i.extname = '.jpg';
            i.fileName = i.fileName + i.extname;
        }
        return i;
    });
    const octokit = initOcto(ctx);
    const ret = [];
    const len = output.length;
    let index = 0;
    async function up() {
        const img = output[index];
        if (index >= len)
            return;
        if (!img) {
            index++;
            return up();
        }
        return octokit
            .upload(img)
            .then(({ imgUrl, sha }) => {
            img.imgUrl = imgUrl;
            img.sha = sha;
            ret.push(img);
            index++;
            return up();
        })
            .catch(e => {
            ctx.log.error(e);
            ctx.emit('notification', {
                title: 'gitee: 上传失败',
                body: e.message,
                text: ''
            });
            index++;
            return up();
        });
    }
    await up();
    ctx.saveConfig({
        [PluginName]: {
            lastSync: helper_1.getNow()
        }
    });
    ctx.output = ret;
    return ctx;
};
async function onRemove(files, { showNotification }) {
    // console.log('1111 =?', this)
    const rms = files.filter(each => each.type === UploaderName);
    if (rms.length === 0)
        return;
    const self = this;
    const ins = initOcto(self);
    const fail = [];
    for (let i = 0; i < rms.length; i++) {
        const each = rms[i];
        await ins.removeFile(each).catch((e) => {
            self.log.error(e);
            fail.push(each);
        });
    }
    if (fail.length) {
        // 确保主线程已经把文件从data.json删掉
        const uploaded = self.getConfig('uploaded');
        uploaded.unshift(...fail);
        self.saveConfig({
            uploaded,
            [PluginName]: {
                lastSync: helper_1.getNow()
            }
        });
    }
    notic(showNotification, '删除提示', fail.length === 0 ? '成功同步删除' : `删除失败${fail.length}个`);
}
const config = (ctx) => {
    let userConfig = ctx.getConfig(`picBed.${UploaderName}`);
    if (!userConfig) {
        userConfig = {};
    }
    const conf = [
        {
            name: 'repo',
            type: 'input',
            default: userConfig.repo || '',
            required: true
        },
        {
            name: 'branch',
            type: 'input',
            default: userConfig.branch || 'master',
            required: false
        },
        {
            name: 'token',
            type: 'input',
            default: userConfig.password || '',
            required: true
        },
        {
            name: 'path',
            type: 'input',
            default: userConfig.path || '',
            required: false
        },
        {
            name: 'customPath',
            type: 'list',
            default: userConfig.customPath || '',
            required: false,
            choices: [
                {
                    name: 'default',
                    value: 'default'
                },
                {
                    name: '年',
                    value: 'year'
                },
                {
                    name: '年月',
                    value: 'yearMonth'
                },
                {
                    name: '年月日',
                    value: 'yearMonthDay'
                }
            ]
        },
        {
            name: 'customUrl',
            type: 'input',
            default: userConfig.customUrl || '',
            required: false
        }
    ];
    return conf;
};
const syncConfig = (ctx) => {
    let userConfig = ctx.getConfig(PluginName);
    if (!userConfig) {
        userConfig = {};
    }
    const conf = [
        {
            name: 'lastSync',
            type: 'input',
            default: userConfig.lastSync || '',
            required: false
        }
    ];
    return conf;
};
module.exports = (ctx) => {
    const register = () => {
        // const { gitee } = ctx.getConfig('picBed')
        // if (!gitee.token) return
        ctx.helper.uploader.register(UploaderName, { handle, config });
        ctx.on('remove', onRemove);
    };
    return {
        register,
        guiMenu,
        uploader: UploaderName,
        config: syncConfig
    };
};
